import { useState } from "react"

export const Contador = () => {

    const [valor, setValor] = useState(0)

    const aumentar = (numero: number) => {
        setValor(valor + numero);
    }

  return (
    <>
        <h3>Contador <small> { valor } </small> </h3>

        <button 
        className="btn btn-primary"
        onClick= { () => aumentar(1) }
        >
            +1
        </button>
        &nbsp;
        <button 
        className="btn btn-primary"
        onClick= { () => aumentar(-1) }
        >
            -1
        </button>
    </>
  )
}
