import { type } from "@testing-library/user-event/dist/type";
import { Fragment, useEffect, useReducer } from "react";

interface AuthState {
    validando: boolean;
    token: string | null;
    username: string;
    nombre: string;
}

const initialState: AuthState = {
    validando: true,
    token: null,
    username: '',
    nombre: ''

}

type LoginPayload = {
    username: string;
    nombre: string;
}

type AuthAction = 
        | { type: 'logout' }
        | { type: 'login', payload: LoginPayload };

const authReducer = (state: AuthState, action: AuthAction): AuthState => {
    switch (action.type) {
        case 'logout':
            return {
                validando: false,
                token: null,
                username: '',
                nombre: ''
            }

        case 'login':
            return {
                validando: false,
                token: 'M0G9G8V9B0B0B0UG9',
                username: action.payload.username,
                nombre: action.payload.nombre
            }

        default:
            return state;
    }
}


export const Login = () => {

    const [state, dispatch] = useReducer(authReducer, initialState);

    useEffect(() => {
        setTimeout(() => {
            dispatch({ type: 'logout' });
        }, 1500);
    }, [])

    // functions
    const login = () => {
        dispatch({ 
            type: 'login',
            payload: {
                nombre: 'Manuel Salinas',
                username: "mxnuelsg"
            }
        })
    }

    const logout = () => {
        dispatch({ type: 'logout' });
    }

    const verify = () => {
        dispatch({ type: 'logout' });
    }


    if (state.validando) {
        return(
            <Fragment>
                <h3>Login</h3>
                <div className="alert alert-info">
                    Validando...
                </div>
            </Fragment>
        )
    }

    return (
      <>
          <h3>Login</h3>

          {
            (state.token)
            ?  <div className="alert alert-success">Autenticado como <b>{state.nombre}</b></div>
            :  <div className="alert alert-danger">No autenticado</div>
          }     

          {
            (state.token)
            ? (
                <button 
                className="btn btn-danger"
                onClick={ logout }
                >
                    Logout
                </button>
            )
            : (
                <>
                    <button
                        className="btn btn-primary"
                        onClick={ login }
                    >
                    Login
                    </button>
                    &nbsp;
                    <button
                        className="btn btn-secondary"
                        onClick={ verify }
                    >
                    Verify
                    </button>
                </>
            ) 
          }          
      </>
    )
  }
