import { useState } from "react"

export const useCounter = (valorInicial: number = 10) => {

    const [valor, setValor] = useState(0)

    // Option 1 as constant 
    const acumular = (numero: number) => {
        setValor(valor + numero);
    }

    return {
        valor,
        acumular
    }
}
