import { Fragment, useEffect, useState } from "react"
import { reqResApi } from "../api/RequestResponse"
import { User, UserListResponse } from "../interfaces/ReqRes";

export const Usuarios = () => {

    const [users, setUsers] = useState<User[]>([]);

    useEffect(() => {
        // API call
        loadUsers();       
    }, [])  

    const loadUsers = () => {
        reqResApi.get<UserListResponse>('/users')
        .then( response => {
            console.log(response.data.data);
            setUsers(response.data.data);
        })
        .catch(console.log);
    }


    const renderUser = ({ id, avatar, first_name, last_name, email }: User) => {
        return(
            <tr key={ id.toString() }>
                <td>
                    <img
                     src= { avatar }
                     alt= { first_name } 
                     style={{
                        width: 80,
                        borderRadius: 40
                     }}
                      />
                </td>
                <td><b>{ first_name }</b> { last_name}</td>
                <td>{ email }</td>
            </tr>
        )
    }

  return (
    <>
        <h3>Usuarios Remotos:</h3>
        <table className="table">
            <thead>
                <tr>
                    <th>Avatar</th>
                    <th>Nombre</th>
                    <th>Email</th>
                </tr>
            </thead>
            <tbody>
              {
                users.map( renderUser )
              }  
            </tbody>
        </table>
    </>
  )
}
